mapper.select:
/*kr.happyjob.study.business.dao.BizPartnerDao.clientlist*/ select client_no ,client_name ,manager_name ,manager_hp ,manager_email ,fax_tel ,client_type from tb_client
client_type = 1
and client_name Like CONCAT('%', #{bpname}, '%')
ORDER BY client_no DESC LIMIT #{pageindex}, #{pageSize}

/*kr.happyjob.study.hwang.dao.HnoticeDao.counthnoticelist*/ SELECT COUNT(1) AS bp_cnt from tb_client
client_type = 1
and client_name Like CONCAT('%', #{bpname}, '%')

/*kr.happyjob.study.business.dao.BizPartnerDao.clientdetail*/ select client_no , permit_no , client_name , manager_name , zip_code , addr , det_addr , manager_hp , manager_email , client_tel , fax_tel , client_type from tb_client
and client_type = 1
and client_no = #{client_no}
and permit_no = #{det_permit_no}

mapper.insert:

    select ifnull(max(client_no),0) + 1 from tb_client

insert into tb_client
        (
        client_no
      , permit_no
      , client_type
      , client_name
      , zip_code
      , addr
      , det_addr
      , manager_name
      , manager_hp
      , client_tel
      , fax_tel
      , manager_email
        )
        values
        (
        #{client_no}
      , #{det_permit_no}
      , 1
      , #{det_client_name}
      , #{det_zip_code}
      , #{det_addr}
      , #{det_det_addr}
      , #{det_manager_name}
      , #{det_manager_hp}
      , #{det_client_tel}
      , #{det_fax_tel}
      , #{det_manager_email}
        )

mapper.update:
		update tb_client
		   set  permit_no = #{det_permit_no}
		      , client_no = #{client_no}
  			  , client_name = #{det_client_name}
     		  , zip_code = #{det_zip_code}
     		  , addr = #{det_addr}
     		  , det_addr = #{det_det_addr}
     		  , manager_name = #{det_manager_name}
    		  , manager_hp = #{det_manager_hp}
    		  , client_tel = #{det_client_tel}
   			  , manager_email = #{det_manager_email}
   			  , fax_tel = #{det_fax_tel}
		 where client_no = #{client_no}
