mapper.select:
/*kr.happyjob.study.business.dao.BmSalePlanDao.empsaleplanlist*/ select sp.plan_date ,ct.client_name ,dc.detail_name as mcname ,pdt.product_name as pdname ,pd.product_name ,sp.goal_amt ,sp.now_amt from tb_salesplan sp inner join tb_client ct ON ct.client_no = sp.client_no inner join tb_detail_code dc ON dc.group_code='lcategory_cd' and dc.detail_code=sp.lcategory_cd inner join tb_product_type pdt on pdt.mcategory_cd = sp.mcategory_cd and pdt.lcategory_cd = sp.lcategory_cd inner join tb_product pd on pd.product_no = sp.product_no
and ct.client_no = #{clientname}
and sp.plan_date >= #{stdate}
and sp.plan_date <= #{eddate}
and pd.lcategory_cd = #{lcategory}
and pd.mcategory_cd = #{mcategory}
and pd.product_no = #{productname}
and sp.loginID = #{loginID}
ORDER BY sp.plan_no desc LIMIT #{pageIndex}, #{pageSize}

/*kr.happyjob.study.business.dao.BmSalePlanDao.countempsaleplan*/
select count(1) as tot_cnt
from tb_salesplan sp
inner join tb_client ct
ON ct.client_no = sp.client_no
inner join tb_detail_code dc ON
dc.group_code='lcategory_cd' and dc.detail_code=sp.lcategory_cd
inner
join tb_product_type pdt on pdt.mcategory_cd = sp.mcategory_cd and
pdt.lcategory_cd = sp.lcategory_cd
inner join tb_product pd on
pd.product_no = sp.product_no


		and ct.client_no =
		#{clientname}


		and sp.plan_date >=
		#{stdate}


		and sp.plan_date <=
		#{eddate}


		and pd.lcategory_cd =
		#{lcategory}


		and pd.mcategory_cd =
		#{mcategory}


		and pd.product_no =
		#{productname}

