mapper.select:
/*kr.happyjob.study.business.dao.EstManagementDao.estmanagementlist*/ select con.contract_no ,con.order_cd ,ui.loginID ,cl.client_name ,con.contract_date ,pd.product_name ,con.tax ,con.price ,con.amt_price ,con.lcategory_cd ,con.mcategory_cd ,pd.product_no ,con.product_amt ,con.total_price from tb_contract con inner join tb_client cl on cl.client_no = con.client_no inner join tb_userinfo ui on ui.loginID = con.loginID inner join tb_product pd on pd.product_no = con.product_no
con.contract_type = '1'
and cl.client_name Like CONCAT('%', #{clientNameSearch}, '%')
and con.contract_date >= #{consdate}
and con.contract_date <= #{conedate}
ORDER BY con.contract_no DESC LIMIT #{pageindex}, #{pageSize}

