mapper.select:
/*kr.happyjob.study.employee.dao.TaApproveDao.taapprovelist*/
select at.atd_no , ui.emp_no as empno , ui.name as empname , at.rest_cd , at.st_date , at.ed_date , at.app_date , at.atd_name , case when at.atd_yn = 'y' then '승인' when at.atd_yn = 'n' then '반려' when at.atd_yn = 'w' then '승인대기' end as atd_yn
from tb_atd_info at inner join tb_userinfo ui on ui.loginID = at.loginID
and at.app_date >= #{appsdate}
and at.app_date <= #{appedate}
and ui.emp_no Like CONCAT('%', #{empno}, '%')
and ui.name Like CONCAT('%', #{empname}, '%')
and at.atd_yn Like CONCAT('%', #{atdyn}, '%')
order by at.atd_no desc LIMIT #{pageindex}, #{pageSize}

		/*kr.happyjob.study.employee.dao.TaApproveDao.counttaApprovelist*/
		SELECT COUNT(1) AS tot_cnt 
         from tb_atd_info at
		      inner join tb_userinfo ui on ui.loginID = at.loginID


                   and at.app_date  >= #{appsdate}


                  and at.app_date <= #{appedate}


                    and ui.emp_no Like CONCAT('%', #{empno}, '%')


                    and ui.name Like CONCAT('%', #{empname}, '%')


                    and at.atd_yn Like CONCAT('%', #{atdyn}, '%')

select ui.dept_cd as deptcd , at.rest_cd , ui.name as empnamepop , ui.emp_no as empnopop , ui.user_type as user_type , ui.hp as tell , at.st_date , at.ed_date , at.rest_rsn , at.app_date , at.atd_yn , at.reject_rsn
from tb_atd_info at inner join tb_userinfo ui on ui.loginID = at.loginID
where at.atd_no = #{atd_no}

mapper.update:



       update tb_atd_info
	         set atd_yn = #{atd_yn}
				,reject_rsn = #{reject_rsn}
				,atd_name = #{name}  

           where atd_no = #{atd_no}
