mapper.insert:

 select ifnull(max(cast((detail_code) as unsigned)),0) + 1  from tb_detail_code where group_code = 'dept_cd'

insert into tb_detail_code
            (
               detail_code
              ,detail_name
              ,group_code
              ,use_yn
            )
     values(

              #{detail_code}
             ,#{detail_name}
             ,'dept_cd'
             ,'Y' 

           );

mapper.update:

		update tb_detail_code
     	   set detail_name = #{detail_name}
   		 where group_code = 'dept_cd' 
   		   and detail_code = #{detail_code}

mapper.delete:
delete from tb_detail_code
 where group_code = 'dept_cd'
   and detail_code = #{detail_code}

mapper.select:
select count(loginID)
  from tb_userinfo
 where dept_cd = #{dept_cd}
