mapper.select:
/*kr.happyjob.study.system.dao.ProductCodeDao.productCodelist*/ select detail_code ,detail_name from tb_detail_code group_code = 'lcategory_cd'
and detail_name Like CONCAT('%', #{searchname}, '%')
ORDER BY detail_code ASC LIMIT #{pageindex}, #{pageSize}

	/*kr.happyjob.study.system.dao.ProductCodeDao.detailproductcode*/
	select detail_code
          ,detail_name
      from tb_detail_code

     	and ( group_code = 'lcategory_cd' and detail_code = #{detail_code})

		or detail_name = #{detail_name}

	/*kr.happyjob.study.system.dao.ProductCodeDao.detailproductcodeCnt*/
	select count(*)
      from tb_detail_code

     	and ( group_code = 'lcategory_cd' and detail_code = #{detail_code})

		or detail_name = #{detail_name}

mapper.delete:
delete from tb_detail_code
      where group_code = 'lcategory_cd'
        and detail_code = #{detail_code}
